import { Connection } from "./connection";
import { EntityManager } from "./entitymanager";
import { ConnectionOption, ELockType } from "./types";
/**
 * 数据库驱动器接口
 * 提供与dialect相关的操作，不同dialect需要实现此接口
 */
export declare abstract class BaseProvider {
    /**
     * 配置
     */
    protected options: object;
    /**
     * 连接池
     */
    protected pool: any;
    /**
     * 数据库 npm 模块
     */
    protected dbMdl: any;
    constructor(cfg: ConnectionOption);
    /**
     * 获取连接
     */
    getConnection(): Promise<any>;
    /**
     * 关闭连接
     * @param connection -    数据库连接对象
     */
    closeConnection(connection: Connection): Promise<void>;
    /**
     * 关闭pool
     */
    endPool(): Promise<void>;
    /**
     * 执行sql语句
     * @param connection -  数据库连接
     * @param sql -         sql语句
     * @param params -      参数
     * @returns             结果(集)
     */
    exec(connection: Connection, sql: string, params?: unknown[] | object): Promise<unknown>;
    /**
     * 处理记录起始记录索引和记录数
     * @param sql -       sql
     * @param start -     开始索引
     * @param limit -     记录数
     * @returns         处理后的sql
     */
    handleStartAndLimit(sql: string, start?: number, limit?: number): string;
    /**
     * 获取实体sequence，针对主键生成策略为sequence时有效
     * @param em -        entity manager
     * @param seqName -   sequence name
     * @param schema -    schema
     * @returns         sequence 值
     */
    getSequenceValue(em: EntityManager, seqName: string, schema?: string): Promise<number>;
    /**
     * 从sql执行结果获取identityid，仅对主键生成策略是identity的有效
     * @param result -  sql执行结果
     * @returns         主键
     */
    getIdentityId(result: unknown): number;
    /**
     * 获取加锁sql语句
     * @param type -      锁类型
     * @param tables -    表名，表锁时使用
     * @param schema -    模式名，表锁时使用
     * @returns         加锁sql语句
     */
    lock(type: ELockType, tables?: string[], schema?: string): string;
    /**
     * 获取释放锁sql语句
     * @param type -      锁类型
     * @returns         释放锁sql语句
     */
    unlock(type: ELockType): string;
    /**
     * 获取新增返回主键字段sql语句
     * @param idField -   主键字段
     * @returns         查询主键sql语句
     */
    insertReturn(idField: string): string;
}
