import { Transaction } from "./transaction";
/**
 * 数据库连接类
 */
declare class Connection {
    /**
     * 真实连接对象
     */
    conn: any;
    /**
     * 线程id
     */
    threadId: number;
    /**
     * 连接状态
     */
    connected: boolean;
    /**
     * 自动提交
     */
    autoCommit: boolean;
    /**
     * 创建者对象id
     */
    fromId: number;
    /**
     * 事务对象
     */
    transaction: Transaction;
    /**
     * 使用次数
     */
    useCount: number;
    /**
     * 构造器
     * @param conn -  实际的连接
     */
    constructor(conn?: any);
    /**
     * 设置实际的connection
     * @param conn -    实际的连接
     */
    setConn(conn: any): void;
    /**
     * 关闭连接
     * @param force -   是否强制关闭
     */
    close(force?: boolean): Promise<void>;
    /**
     * 创建事务对象
     */
    createTransaction(): Transaction;
}
export { Connection };
