import { EntityPKey, EntityColumnOption, EntityRelation, EntityRefColumn, JoinTableOption } from "../types";
/**
 * 装饰器（注解类）
 */
/**
 
 * Entity装饰器，装饰实体(表)，装饰类
 * @param tblName -   表名
 * @param schema -    数据库名
 */
declare function Entity(tblName: string, schema?: string): (target: any) => void;
/**
 
 * 主键装饰器，装饰属性
 * @param cfg -       配置项
 */
declare function Id(cfg?: EntityPKey): (target: unknown, propertyName: string) => void;
/**
 
 * 字段装饰器，装饰属性
 * @param cfg - 配置项
 */
declare function Column(cfg: EntityColumnOption): (target: unknown, propertyName: string) => void;
/**
 
 * 版本号装饰器，乐观锁时有效，装饰属性
 */
declare function Version(): (target: unknown, propertyName: string) => void;
/**
 
 * 字段装饰器，装饰属性
 * @param cfg - 配置项
 */
declare function JoinTable(cfg: JoinTableOption): (target: unknown, propertyName: string) => void;
/**
 
 * 字段装饰器，装饰属性
 * @param cfg - 配置项
 */
declare function JoinColumn(cfg: EntityRefColumn): (target: unknown, propertyName: string) => void;
/**
 
 * 一对多关系，装饰属性
 * @param cfg -   实体关系配置
 */
declare function OneToMany(cfg: EntityRelation): (target: unknown, propertyName: string) => void;
/**
 
 * 一对一关系，装饰属性
 * @param cfg -   实体关系配置
 */
declare function OneToOne(cfg: EntityRelation): (target: unknown, propertyName: string) => void;
/**
 * 多对一关系，装饰属性
 * @param cfg -   实体关系配置
 */
declare function ManyToOne(cfg: EntityRelation): (target: unknown, propertyName: string) => void;
/**
 * 多对多关系，装饰属性
 * @param cfg -   实体关系配置
 */
declare function ManyToMany(cfg: EntityRelation): (target: unknown, propertyName: string) => void;
export { Entity, Id, Column, Version, JoinTable, JoinColumn, OneToMany, OneToOne, ManyToOne, ManyToMany };
